from collections import deque

m, n = map(int, input().split())
matrix = [input().strip() for i in range(m)]
visited = [[False] * n for i in range(m)]
count = 0
for i in range(m):
    for j in range(n):
        if matrix[i][j] == '#' and not visited[i][j]:
            count += 1
            queue = deque()
            queue.append((i, j))
            visited[i][j] = True
            while queue:
                x, y = queue.popleft()
                for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                    nx, ny = x + dx, y + dy
                    if -1 < nx < m and -1 < ny < n:
                        if matrix[nx][ny] == '#' and not visited[nx][ny]:
                            visited[nx][ny] = True
                            queue.append((nx, ny))
print(count)
